import { useState } from "react";
import { Mail, Lock, Github, Linkedin, User, CheckCircle } from "lucide-react";

export default function LoginComponent() {
  const [currentView, setCurrentView] = useState('login'); // 'login' or 'signup'
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState(''); // 'success' or 'error'
  const [validationErrors, setValidationErrors] = useState({});
  const [user, setUser] = useState(null);
  const [isHovered, setIsHovered] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear validation error for this field when user starts typing
    if (validationErrors[name]) {
      setValidationErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    console.log('Login button clicked');
    
    setIsLoading(true);
    setMessage('');
    setValidationErrors({});

    const requestData = {
      email: formData.email,
      password: formData.password
    };

    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData)
      });

      const data = await response.json();

      if (response.ok && data.success) {
        setMessage(`Welcome back, ${data.user.fullName}!`);
        setMessageType('success');
        setUser(data.user);
        
        // Reset form
        setFormData({
          email: '',
          password: '',
          confirmPassword: '',
          firstName: '',
          lastName: ''
        });
      } else {
        setMessage(data.message || 'Login failed. Please check your credentials.');
        setMessageType('error');
      }
    } catch (error) {
      console.error('Login error:', error);
      setMessage('Unable to connect to server. Please try again later.');
      setMessageType('error');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    console.log('Signup button clicked');
    
    setIsLoading(true);
    setMessage('');
    setValidationErrors({});

    // Client-side validation
    if (formData.password !== formData.confirmPassword) {
      setMessage('Passwords do not match.');
      setMessageType('error');
      setIsLoading(false);
      return;
    }

    if (formData.password.length < 6) {
      setMessage('Password must be at least 6 characters long.');
      setMessageType('error');
      setIsLoading(false);
      return;
    }

    // Check if all required fields are filled
    if (!formData.firstName || !formData.lastName || !formData.email || !formData.password) {
      setMessage('Please fill in all required fields.');
      setMessageType('error');
      setIsLoading(false);
      return;
    }

    const requestData = {
      firstName: formData.firstName,
      lastName: formData.lastName,
      email: formData.email,
      password: formData.password,
      confirmPassword: formData.confirmPassword
    };

    try {
      const response = await fetch('http://localhost:8080/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestData)
      });

      const data = await response.json();

      if (response.ok && data.success) {
        setMessage(`Account created successfully for ${data.user.fullName}! Please login.`);
        setMessageType('success');
        setTimeout(() => {
          setCurrentView('login');
          setFormData({ 
            email: formData.email, 
            password: '', 
            confirmPassword: '', 
            firstName: '', 
            lastName: '' 
          });
          setMessage('');
        }, 2000);
      } else {
        if (data.errors) {
          // Handle field-specific validation errors
          setValidationErrors(data.errors);
          setMessage('Please fix the errors below.');
        } else {
          setMessage(data.message || 'Registration failed. Please try again.');
        }
        setMessageType('error');
      }
    } catch (error) {
      console.error('Signup error:', error);
      setMessage('Unable to connect to server. Please try again later.');
      setMessageType('error');
    } finally {
      setIsLoading(false);
    }
  };

  const handleLogout = () => {
    setUser(null);
    setMessage('');
    setFormData({
      email: '',
      password: '',
      confirmPassword: '',
      firstName: '',
      lastName: ''
    });
    setCurrentView('login');
  };

  const switchView = (view) => {
    setCurrentView(view);
    setMessage('');
    setValidationErrors({});
    setFormData({
      email: '',
      password: '',
      confirmPassword: '',
      firstName: '',
      lastName: ''
    });
  };

  // Common styles
  const containerStyle = {
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient(135deg, #f3e8ff, #fdf2f8, #fed7aa)',
    padding: '24px'
  };

  const cardStyle = {
    width: '100%',
    maxWidth: currentView === 'signup' ? '32rem' : '28rem',
    backdropFilter: 'blur(12px)',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    border: '1px solid #e5e7eb',
    boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)',
    borderRadius: '16px',
    overflow: 'hidden',
    transform: isHovered ? 'scale(1.02)' : 'scale(1)',
    transition: 'transform 0.3s ease'
  };

  const inputContainerStyle = {
    display: 'flex',
    alignItems: 'center',
    backgroundColor: '#f9fafb',
    borderRadius: '12px',
    padding: '8px',
    boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)',
    border: '1px solid #e5e7eb',
    marginBottom: '16px'
  };

  const inputStyle = {
    border: 'none',
    backgroundColor: 'transparent',
    outline: 'none',
    width: '100%',
    padding: '8px',
    fontSize: '14px'
  };

  const buttonStyle = {
    width: '100%',
    background: 'linear-gradient(90deg, #ec4899, #f97316)',
    color: 'white',
    fontWeight: '600',
    padding: '12px 24px',
    borderRadius: '12px',
    border: 'none',
    cursor: 'pointer',
    boxShadow: '0 10px 25px -5px rgba(0, 0, 0, 0.1)',
    transition: 'opacity 0.3s ease, transform 0.2s ease',
    fontSize: '16px'
  };

  const socialButtonStyle = {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '8px',
    padding: '10px 16px',
    border: '1px solid #d1d5db',
    borderRadius: '8px',
    backgroundColor: 'white',
    cursor: 'pointer',
    transition: 'transform 0.2s ease',
    fontSize: '14px'
  };

  // If user is logged in, show dashboard
  if (user) {
    return (
      <div style={containerStyle}>
        <div 
          style={cardStyle}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
        >
          <div style={{ padding: '32px', textAlign: 'center' }}>
            <div style={{
              margin: '0 auto 16px',
              width: '64px',
              height: '64px',
              backgroundColor: '#dcfce7',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              <CheckCircle style={{ width: '32px', height: '32px', color: '#16a34a' }} />
            </div>
            <h1 style={{ fontSize: '24px', fontWeight: '700', color: '#111827', marginBottom: '8px' }}>
              Welcome, {user.fullName}!
            </h1>
            <p style={{ color: '#6b7280', marginBottom: '24px' }}>
              You have successfully logged in.
            </p>
            <div style={{ backgroundColor: '#f9fafb', borderRadius: '8px', padding: '16px', marginBottom: '24px' }}>
              <p style={{ fontSize: '14px', color: '#374151', margin: '4px 0' }}>
                <strong>Email:</strong> {user.email}
              </p>
              <p style={{ fontSize: '14px', color: '#374151', margin: '4px 0' }}>
                <strong>User ID:</strong> {user.id}
              </p>
            </div>
            <button
              onClick={handleLogout}
              style={{
                backgroundColor: '#ef4444',
                color: 'white',
                padding: '12px 24px',
                borderRadius: '8px',
                border: 'none',
                fontSize: '16px',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'background-color 0.2s'
              }}
              onMouseOver={(e) => e.target.style.backgroundColor = '#dc2626'}
              onMouseOut={(e) => e.target.style.backgroundColor = '#ef4444'}
            >
              Logout
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={containerStyle}>
      <div 
        style={cardStyle}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        <div style={{ padding: '32px' }}>
          {/* Header */}
          <div style={{ textAlign: 'center', marginBottom: '32px' }}>
            <div style={{
              margin: '0 auto 16px',
              width: '64px',
              height: '64px',
              backgroundColor: '#e0e7ff',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              <User style={{ width: '32px', height: '32px', color: '#4f46e5' }} />
            </div>
            <h2 style={{
              fontSize: '30px',
              fontWeight: 'bold',
              color: '#1f2937',
              marginBottom: '8px'
            }}>
              {currentView === 'login' ? 'Welcome Back' : 'Create Account'}
            </h2>
            <p style={{ color: '#6b7280', marginBottom: '0' }}>
              {currentView === 'login' ? 'Sign in to continue your journey 🚀' : 'Join us and start your journey today ✨'}
            </p>
          </div>

          {/* Forms */}
          <form onSubmit={currentView === 'login' ? handleLogin : handleSignup}>
            {/* Signup specific fields */}
            {currentView === 'signup' && (
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px', marginBottom: '16px' }}>
                <div style={inputContainerStyle}>
                  <User style={{ color: '#6b7280', marginRight: '8px' }} size={18} />
                  <input
                    type="text"
                    name="firstName"
                    placeholder="First Name"
                    value={formData.firstName}
                    onChange={handleInputChange}
                    style={{
                      ...inputStyle,
                      borderColor: validationErrors.firstName ? '#ef4444' : 'transparent'
                    }}
                    required
                  />
                </div>
                <div style={inputContainerStyle}>
                  <User style={{ color: '#6b7280', marginRight: '8px' }} size={18} />
                  <input
                    type="text"
                    name="lastName"
                    placeholder="Last Name"
                    value={formData.lastName}
                    onChange={handleInputChange}
                    style={{
                      ...inputStyle,
                      borderColor: validationErrors.lastName ? '#ef4444' : 'transparent'
                    }}
                    required
                  />
                </div>
              </div>
            )}

            {/* Email Input */}
            <div style={inputContainerStyle}>
              <Mail style={{ color: '#6b7280', marginRight: '8px' }} size={18} />
              <input
                type="email"
                name="email"
                placeholder="Email Address"
                value={formData.email}
                onChange={handleInputChange}
                style={{
                  ...inputStyle,
                  borderColor: validationErrors.email ? '#ef4444' : 'transparent'
                }}
                required
              />
            </div>

            {/* Password Input */}
            <div style={inputContainerStyle}>
              <Lock style={{ color: '#6b7280', marginRight: '8px' }} size={18} />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleInputChange}
                style={{
                  ...inputStyle,
                  borderColor: validationErrors.password ? '#ef4444' : 'transparent'
                }}
                required
              />
            </div>

            {/* Confirm Password for Signup */}
            {currentView === 'signup' && (
              <div style={inputContainerStyle}>
                <Lock style={{ color: '#6b7280', marginRight: '8px' }} size={18} />
                <input
                  type="password"
                  name="confirmPassword"
                  placeholder="Confirm Password"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  style={{
                    ...inputStyle,
                    borderColor: validationErrors.confirmPassword ? '#ef4444' : 'transparent'
                  }}
                  required
                />
              </div>
            )}

            {/* Remember me & Forgot password (Login only) */}
            {currentView === 'login' && (
              <div style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                marginBottom: '24px',
                fontSize: '14px'
              }}>
                <label style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                  <input type="checkbox" style={{ borderRadius: '4px' }} />
                  <span>Remember me</span>
                </label>
                <a href="#" style={{
                  color: '#ec4899',
                  textDecoration: 'none'
                }}>
                  Forgot password?
                </a>
              </div>
            )}

            {/* Message Display */}
            {message && (
              <div style={{
                padding: '12px',
                borderRadius: '8px',
                fontSize: '14px',
                marginBottom: '16px',
                backgroundColor: messageType === 'success' ? '#f0fdf4' : '#fef2f2',
                color: messageType === 'success' ? '#166534' : '#991b1b',
                border: `1px solid ${messageType === 'success' ? '#bbf7d0' : '#fecaca'}`
              }}>
                {message}
              </div>
            )}

            {/* Submit Button */}
            <button 
              type="submit"
              disabled={isLoading}
              style={{
                ...buttonStyle,
                opacity: isLoading ? 0.7 : 1,
                cursor: isLoading ? 'not-allowed' : 'pointer'
              }}
              onMouseOver={(e) => {
                if (!isLoading) e.target.style.opacity = '0.9';
              }}
              onMouseOut={(e) => {
                if (!isLoading) e.target.style.opacity = '1';
              }}
            >
              {isLoading ? (
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                  <div style={{
                    width: '20px',
                    height: '20px',
                    border: '2px solid transparent',
                    borderTop: '2px solid white',
                    borderRadius: '50%',
                    animation: 'spin 1s linear infinite',
                    marginRight: '12px'
                  }}></div>
                  {currentView === 'login' ? 'Signing in...' : 'Creating account...'}
                </div>
              ) : (
                currentView === 'login' ? 'Sign In' : 'Create Account'
              )}
            </button>
          </form>

          {/* Social Login (Login only) */}
          {currentView === 'login' && (
            <>
              <div style={{
                margin: '24px 0',
                display: 'flex',
                alignItems: 'center',
                gap: '8px'
              }}>
                <div style={{ flexGrow: 1, height: '1px', backgroundColor: '#d1d5db' }}></div>
                <span style={{ color: '#6b7280', fontSize: '14px' }}>or continue with</span>
                <div style={{ flexGrow: 1, height: '1px', backgroundColor: '#d1d5db' }}></div>
              </div>

              <div style={{ display: 'flex', gap: '16px' }}>
                <button 
                  type="button"
                  style={socialButtonStyle}
                  onMouseOver={(e) => e.target.style.transform = 'scale(1.05)'}
                  onMouseOut={(e) => e.target.style.transform = 'scale(1)'}
                >
                  <Github size={18} /> Github
                </button>
                <button 
                  type="button"
                  style={socialButtonStyle}
                  onMouseOver={(e) => e.target.style.transform = 'scale(1.05)'}
                  onMouseOut={(e) => e.target.style.transform = 'scale(1)'}
                >
                  <Linkedin size={18} /> LinkedIn
                </button>
              </div>
            </>
          )}

          {/* Switch View Link */}
          <p style={{
            textAlign: 'center',
            fontSize: '14px',
            color: '#6b7280',
            marginTop: '24px',
            marginBottom: '0'
          }}>
            {currentView === 'login' ? "Don't have an account? " : "Already have an account? "}
            <button
              type="button"
              onClick={() => switchView(currentView === 'login' ? 'signup' : 'login')}
              style={{
                color: '#ec4899',
                fontWeight: '600',
                textDecoration: 'none',
                background: 'none',
                border: 'none',
                cursor: 'pointer',
                fontSize: '14px'
              }}
            >
              {currentView === 'login' ? 'Sign up here' : 'Sign in here'}
            </button>
          </p>
        </div>
      </div>

      <style>{`
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
        
        @media (max-width: 640px) {
          [style*="grid-template-columns: 1fr 1fr"] {
            display: block !important;
          }
          
          [style*="grid-template-columns: 1fr 1fr"] > div:not(:last-child) {
            margin-bottom: 1rem !important;
          }
        }
      `}</style>
    </div>
  );
}
