import React, { useState, useEffect } from 'react';

function App() {
  const [currentView, setCurrentView] = useState('login'); // 'login', 'signup', 'forgot', 'reset'
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    newPassword: ''
  });
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState(''); // 'success' or 'error'
  const [validationErrors, setValidationErrors] = useState({});
  const [user, setUser] = useState(null);
  const [isHovered, setIsHovered] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(() => {
    const savedTheme = window.localStorage?.getItem('theme');
    return savedTheme === 'dark';
  });
  const [resetTokenEmail, setResetTokenEmail] = useState(''); // store email for reset

  useEffect(() => {
    if (typeof window !== 'undefined' && window.localStorage) {
      window.localStorage.setItem('theme', isDarkMode ? 'dark' : 'light');
    }
  }, [isDarkMode]);

  const toggleDarkMode = () => setIsDarkMode(!isDarkMode);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (validationErrors[name]) {
      setValidationErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  // ------------------- API HANDLERS ------------------- //

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true); setMessage(''); setValidationErrors({});
    try {
      const response = await fetch('http://localhost:8080/api/auth/login', {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: formData.email, password: formData.password })
      });
      const data = await response.json();
      if (response.ok && data.success) {
        setMessage(`Welcome back, ${data.user.fullName}!`);
        setMessageType('success');
        setUser(data.user);
        setFormData({ email: '', password: '', confirmPassword: '', firstName: '', lastName: '', newPassword: '' });
      } else {
        setMessage(data.message || 'Login failed.');
        setMessageType('error');
      }
    } catch (error) {
      console.error(error);
      setMessage('Unable to connect to server.');
      setMessageType('error');
    } finally { setIsLoading(false); }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setIsLoading(true); setMessage(''); setValidationErrors({});
    if (formData.password !== formData.confirmPassword) {
      setMessage('Passwords do not match.'); setMessageType('error'); setIsLoading(false); return;
    }
    if (formData.password.length < 6) {
      setMessage('Password must be at least 6 characters long.'); setMessageType('error'); setIsLoading(false); return;
    }
    if (!formData.firstName || !formData.lastName || !formData.email || !formData.password) {
      setMessage('Please fill in all required fields.'); setMessageType('error'); setIsLoading(false); return;
    }
    try {
      const response = await fetch('http://localhost:8080/api/auth/register', {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      if (response.ok && data.success) {
        setMessage(`Account created for ${data.user.fullName}! Please login.`); setMessageType('success');
        setTimeout(() => {
          setCurrentView('login');
          setFormData({ email: formData.email, password: '', confirmPassword: '', firstName: '', lastName: '', newPassword: '' });
          setMessage('');
        }, 2000);
      } else {
        if (data.errors) { setValidationErrors(data.errors); setMessage('Please fix the errors.'); }
        else setMessage(data.message || 'Registration failed.');
        setMessageType('error');
      }
    } catch (error) {
      console.error(error);
      setMessage('Unable to connect to server.');
      setMessageType('error');
    } finally { setIsLoading(false); }
  };

  const handleLogout = () => {
    setUser(null); setMessage('');
    setFormData({ email: '', password: '', confirmPassword: '', firstName: '', lastName: '', newPassword: '' });
    setCurrentView('login');
  };

  const handleForgotPassword = async (e) => {
    e.preventDefault();
    if (!formData.email) { setMessage('Please enter your email.'); setMessageType('error'); return; }
    setResetTokenEmail(formData.email);
    setFormData({ ...formData, password: '', confirmPassword: '', newPassword: '' });
    setMessage('Enter your new password.'); setMessageType('success');
    setCurrentView('reset');
  };

  const handleResetPassword = async (e) => {
    e.preventDefault();
    if (!formData.newPassword || !formData.confirmPassword) { setMessage('Fill all fields.'); setMessageType('error'); return; }
    if (formData.newPassword !== formData.confirmPassword) { setMessage('Passwords do not match.'); setMessageType('error'); return; }
    try {
      const response = await fetch('http://localhost:8080/api/auth/reset-password', {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email: resetTokenEmail, newPassword: formData.newPassword, confirmPassword: formData.confirmPassword })
      });
      const data = await response.json();
      if (response.ok && data.success) {
        setMessage('Password reset successfully!'); setMessageType('success');
        setCurrentView('login');
        setFormData({ email: resetTokenEmail, password: '', confirmPassword: '', firstName: '', lastName: '', newPassword: '' });
      } else { setMessage(data.message || 'Reset failed.'); setMessageType('error'); }
    } catch (error) {
      console.error(error);
      setMessage('Server error. Try later.'); setMessageType('error');
    }
  };

  const switchView = (view) => {
    setCurrentView(view); setMessage(''); setValidationErrors({});
    setFormData({ email: '', password: '', confirmPassword: '', firstName: '', lastName: '', newPassword: '' });
  };

  // ------------------- ICONS ------------------- //
  const MailIcon = () => (
    <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"/>
      <polyline points="22,6 12,13 2,6"/>
    </svg>
  );
  const LockIcon = () => (
    <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <rect x="3" y="11" width="18" height="11" rx="2" ry="2"/>
      <circle cx="12" cy="7" r="4"/>
      <path d="M12 1v6"/>
    </svg>
  );
  const UserIcon = () => (
    <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
      <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
      <circle cx="12" cy="7" r="4"/>
    </svg>
  );

  // ------------------- THEME ------------------- //
  const getThemeColors = () => {
    if (isDarkMode) {
      return {
        background: 'linear-gradient(135deg, #1e1b4b, #312e81, #1f2937)',
        cardBg: 'rgba(31, 41, 55, 0.9)',
        cardBorder: '#374151',
        textPrimary: '#f9fafb',
        textSecondary: '#d1d5db',
        inputBg: '#374151',
        inputBorder: '#4b5563',
        inputFocus: '#6366f1',
        successBg: '#064e3b',
        successText: '#6ee7b7',
        successBorder: '#047857',
        errorBg: '#7f1d1d',
        errorText: '#fca5a5',
        errorBorder: '#dc2626',
      };
    } else {
      return {
        background: 'linear-gradient(135deg, #f3e8ff, #fdf2f8, #fed7aa)',
        cardBg: 'rgba(255, 255, 255, 0.9)',
        cardBorder: '#e5e7eb',
        textPrimary: '#111827',
        textSecondary: '#6b7280',
        inputBg: '#f9fafb',
        inputBorder: '#e5e7eb',
        inputFocus: '#4f46e5',
        successBg: '#f0fdf4',
        successText: '#166534',
        successBorder: '#bbf7d0',
        errorBg: '#fef2f2',
        errorText: '#991b1b',
        errorBorder: '#fecaca',
      };
    }
  };
  const theme = getThemeColors();
  const containerStyle = { minHeight:'100vh', display:'flex', alignItems:'center', justifyContent:'center', background:theme.background, padding:'24px', fontFamily:'-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif', position:'relative' };
  const cardStyle = { width:'100%', maxWidth: currentView==='signup'?'32rem':'28rem', backdropFilter:'blur(12px)', backgroundColor:theme.cardBg, border:`1px solid ${theme.cardBorder}`, borderRadius:'16px', overflow:'hidden', transform: isHovered?'scale(1.02)':'scale(1)', transition:'transform 0.3s ease', padding:'32px' };
  const inputContainerStyle = { display:'flex', alignItems:'center', backgroundColor:theme.inputBg, borderRadius:'12px', padding:'8px 12px', border:`1px solid ${theme.inputBorder}`, marginBottom:'16px' };
  const inputStyle = { border:'none', backgroundColor:'transparent', outline:'none', width:'100%', padding:'8px', fontSize:'14px', fontFamily:'inherit', color:theme.textPrimary };
  const buttonStyle = { width:'100%', background:'linear-gradient(90deg, #ec4899, #f97316)', color:'white', fontWeight:'600', padding:'12px 24px', borderRadius:'12px', border:'none', cursor:'pointer', fontSize:'16px', fontFamily:'inherit' };

  // ------------------- RENDER ------------------- //

  if (user) {
    return (
      <div style={containerStyle}>
        <div style={cardStyle}>
          <h1 style={{color:theme.textPrimary}}>Welcome, {user.fullName}!</h1>
          <button onClick={handleLogout} style={{marginTop:'24px', padding:'12px 24px', borderRadius:'8px', backgroundColor:'#ef4444', color:'white', border:'none', cursor:'pointer'}}>Logout</button>
        </div>
      </div>
    );
  }

  return (
    <div style={containerStyle}>
      <div style={cardStyle}>
        <h2 style={{ textAlign:'center', marginBottom:'24px', color:theme.textPrimary }}>
          {currentView==='login'?'Login':currentView==='signup'?'Sign Up':currentView==='forgot'?'Forgot Password':'Reset Password'}
        </h2>

        {message && <div style={{marginBottom:'16px', padding:'12px', borderRadius:'8px', backgroundColor:messageType==='success'?theme.successBg:theme.errorBg, color:messageType==='success'?theme.successText:theme.errorText}}>{message}</div>}

        {/* LOGIN FORM */}
        {currentView==='login' && (
          <form onSubmit={handleLogin}>
            <div style={inputContainerStyle}><MailIcon /><input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleInputChange} style={inputStyle} required /></div>
            <div style={inputContainerStyle}><LockIcon /><input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleInputChange} style={inputStyle} required /></div>
            <div style={{display:'flex', justifyContent:'space-between', marginBottom:'16px'}}>
              <label><input type="checkbox" /> Remember me</label>
              <a href="#" style={{color:'#ec4899'}} onClick={(e)=>{ e.preventDefault(); setCurrentView('forgot'); setMessage(''); setFormData({ email:'', password:'', confirmPassword:'', firstName:'', lastName:'', newPassword:'' }); }}>Forgot password?</a>
            </div>
            <button type="submit" style={buttonStyle}>Login</button>
            <p style={{textAlign:'center', marginTop:'16px'}}>Don't have an account? <button type="button" onClick={()=>switchView('signup')} style={{color:'#ec4899', background:'none', border:'none', cursor:'pointer'}}>Sign up</button></p>
          </form>
        )}

        {/* SIGNUP FORM */}
        {currentView==='signup' && (
          <form onSubmit={handleSignup}>
            <div style={{display:'flex', gap:'8px', marginBottom:'16px'}}>
              <div style={inputContainerStyle}><input type="text" name="firstName" placeholder="First Name" value={formData.firstName} onChange={handleInputChange} style={inputStyle} required /></div>
              <div style={inputContainerStyle}><input type="text" name="lastName" placeholder="Last Name" value={formData.lastName} onChange={handleInputChange} style={inputStyle} required /></div>
            </div>
            <div style={inputContainerStyle}><input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleInputChange} style={inputStyle} required /></div>
            <div style={inputContainerStyle}><input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleInputChange} style={inputStyle} required /></div>
            <div style={inputContainerStyle}><input type="password" name="confirmPassword" placeholder="Confirm Password" value={formData.confirmPassword} onChange={handleInputChange} style={inputStyle} required /></div>
            <button type="submit" style={buttonStyle}>Sign Up</button>
            <p style={{textAlign:'center', marginTop:'16px'}}>Already have an account? <button type="button" onClick={()=>switchView('login')} style={{color:'#ec4899', background:'none', border:'none', cursor:'pointer'}}>Login</button></p>
          </form>
        )}

        {/* FORGOT PASSWORD */}
        {currentView==='forgot' && (
          <form onSubmit={handleForgotPassword}>
            <div style={inputContainerStyle}><MailIcon /><input type="email" name="email" placeholder="Enter your email" value={formData.email} onChange={handleInputChange} style={inputStyle} required /></div>
            <button type="submit" style={buttonStyle}>Continue</button>
            <p style={{textAlign:'center', marginTop:'16px'}}><button type="button" onClick={()=>switchView('login')} style={{color:'#ec4899', background:'none', border:'none', cursor:'pointer'}}>Back to Login</button></p>
          </form>
        )}

        {/* RESET PASSWORD */}
        {currentView==='reset' && (
          <form onSubmit={handleResetPassword}>
            <div style={inputContainerStyle}><LockIcon /><input type="password" name="newPassword" placeholder="New Password" value={formData.newPassword} onChange={(e)=>setFormData({...formData,newPassword:e.target.value})} style={inputStyle} required /></div>
            <div style={inputContainerStyle}><LockIcon /><input type="password" name="confirmPassword" placeholder="Confirm Password" value={formData.confirmPassword} onChange={(e)=>setFormData({...formData,confirmPassword:e.target.value})} style={inputStyle} required /></div>
            <button type="submit" style={buttonStyle}>Reset Password</button>
            <p style={{textAlign:'center', marginTop:'16px'}}><button type="button" onClick={()=>switchView('login')} style={{color:'#ec4899', background:'none', border:'none', cursor:'pointer'}}>Back to Login</button></p>
          </form>
        )}

        <div style={{position:'absolute', top:'16px', right:'16px'}}>
          <button onClick={toggleDarkMode} style={{border:'none', background:'none', cursor:'pointer', color:theme.textPrimary}}>{isDarkMode?'☀️':'🌙'}</button>
        </div>
      </div>
    </div>
  );
}

export default App;
